import { datos } from '../bodycomponents/cards/cards.component'



  export interface CartItem {
    id: string;
    name: string;
    price: number;
    quantity: number;
  }
  
  export interface CartState {
    items: CartItem[];
    totalAmount: number;
  }
  
  export const initialCartState: CartState = {
    items: [],
    totalAmount: 0,
  };


export interface Appstate {
   productos: datos[];
   grandTotal: number;
}
