import { Component } from '@angular/core';
import { PeticionService } from 'src/app/servicios/peticion.service';


declare let $:any
declare let Swal:any


@Component({
  selector: 'app-misdatos',
  templateUrl: './misdatos.component.html',
  styleUrls: ['./misdatos.component.css']
})
export class MisdatosComponent implements{


  ngOnInit(): void {
    this.cargarTodas
    this.cargarTodas()
  }

  constructor (private peticion:PeticionService){}


  email:string = ""
  password:any = ""
  nombre:string = ""
  rol:number = 2
  direccion: any = ""
  telefono:any = ""
  estado:number = 1
  data:any[] = []
  Idusuario:String =""


  cargarTodas(){

    let post = {
      host:this.peticion.urlHost,
      path:"/usuarios/misdatos",
      payload:{
        email:this.email
      }
    }
  this.peticion.Post(post.host+post.path, post.payload).then(
    (res:any) => {
      console.log(res)
      this.data = res.data
    }
  )
 }
 ActualizarDatos(id:string){
  this.Idusuario =id
  let post = {
    host:this.peticion.urlHost,
    path:"/usuarios/actualizar",

  }
this.peticion.get(post.host+post.path + "/" + this.Idusuario).then(
  (res:any) => {
    console.log(res)
    this.email = res.data[0].email
    this.nombre = res.data[0].nombre
    this.estado = res.data[0].estado
    $('#modalNuevo').modal('show')
  }
)

}
}
