import { createSelector, createFeatureSelector } from '@ngrx/store';
export const selectCart = createFeatureSelector('cart');
export const selectCartItems = createSelector(selectCart, state => state.items);
export const selectCartTotalAmount = createSelector(selectCart, state => state.totalAmount);
export const selectCartTotalQuantity = createSelector(selectCartItems, items => items.reduce((total, item) => total + item.quantity, 0));
