import { Component, Host, OnInit } from '@angular/core';
import { PeticionService } from 'src/app/servicios/peticion.service';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent {

  // constructor(private peticion:PeticionService, private router: Router)

//   nombre:string ="cargando..."
//   rol:string ="cargando..."

// cargarestado(){

//   let post = {
//     Host:Peticion.urlHost
//   }
// }

statemenu:boolean = true

Switchmenu(){

  if(this.statemenu == true){
    this.statemenu = false
    let mimenu:any = document.getElementById("menu-box-dsb")
    mimenu.style.display = 'none'
    let ventana:any = document.getElementsByClassName("window")
    ventana[0].style.width = '99%'
    let btnmn:any = document.getElementById("btnmenu")
    btnmn.style.marginLeft = '18px'
   }
  else{
    this.statemenu = true
    let mimenu:any = document.getElementById("menu-box-dsb")
    mimenu.style.display = 'block'
    let ventana:any = document.getElementsByClassName("window")
    ventana[0].style.width = 'calc(100% - 230px)'
    let btnmn:any = document.getElementById("btnmenu")
    btnmn.style.marginLeft = '227px'
  }

}
}
