import { ActionReducer, MetaReducer } from '@ngrx/store';
import { localStorageSync } from '@ngrx/store-localstorage';
import { CartState } from './cart/cart.model'; // Import your app state if necessary

export function localStorageSyncReducer(reducer: ActionReducer<CartState>): ActionReducer<CartState> {
  return localStorageSync({ keys: ['cart'] })(reducer);
}

export const metaReducers: Array<MetaReducer<CartState, any>> = [localStorageSyncReducer];
