// }
EditarId(id:string){
  this.Idu =id
  let post = {
    host:this.peticion.urlHost,
    path:"/usuarios/listarid",

  }
this.peticion.get(post.host+post.path + "/" + this.Idu).then(
  (res:any) => {
    console.log(res)
    this.email = res.data[0].email
    this.nombre = res.data[0].nombre
    this.estado = res.data[0].estado
    $('#modalNuevo').modal('show')
  }
)

ActualizarDatos(){

  let post = {
    host:this.peticion.urlHost,
    path:"/usuarios/actualizardatos",
    payload:{

      email:this.email,
      nombre:this.nombre,
      estado:this.estado,
      direccion:this.direccion,
      telefono:this.telefono,

    }
  }
this.peticion.Post(post.host+post.path, post.payload).then(
  (res:any) => {
    console.log(res)
    if(res.state == true){

      Swal.fire({
        icon: "success",
        title: "Que Bien!",
        text: res.mensaje,
      });

      $('#modalNuevo').modal('hide')
      this.cargarTodas()
    }
    else{
      Swal.fire({
        icon: "error",
        title: "Ouchh.",
        text: res.mensaje,
      });
    }
  })
}

}
